import axios from "axios";

export const setAuthToken = token => {
  if (token) {
    // Apply to all request
    axios.defaults.headers.common["Authorization"] = token;
  } else {
    // Delete auth header
    axios.defaults.headers.common["Authorization"];
  }
};

export default setAuthToken;
